document.addEventListener("DOMContentLoaded",()=>{"use strict";const tabs=document.querySelectorAll(".recommend-tabs__item"),contents=document.querySelectorAll(".recommend-content");for(let tab of tabs)tab.addEventListener("click",showContent);function showContent(){for(let content of contents)content.classList.remove("recommend-content--show");for(let tab of tabs)tab.classList.remove("recommend-tabs__item--active");if(window.screen.width>576){const tab=this;tab.classList.add("recommend-tabs__item--active");const tabAttr=tab.getAttribute("data-tab"),content=document.getElementById(`${tabAttr}`);content.classList.add("recommend-content--show")}else window.location.href="https://mywebdiving.ru/UNIVERSAL/article-freelance.html"}let menuBtn=document.querySelector(".menu-button"),menu=document.querySelector(".menu"),menuLines=document.querySelectorAll(".menu-button__line"),menuLink;function openMenu(event){event.preventDefault(),menuLines.forEach(item=>{item.classList.toggle("menu-button__line--active")}),menu.classList.toggle("menu--visible")}function closeMenu(event){event.preventDefault(),menu.classList.remove("menu--visible"),menuLines.forEach(item=>{item.classList.toggle("menu-button__line--active")})}document.querySelectorAll(".menu-list__item-link").forEach(item=>{item.addEventListener("click",closeMenu)}),menuBtn.addEventListener("click",openMenu);const openModalBtn=document.querySelectorAll("[data-toggle=modal]"),closeModalBtn=document.querySelector(".modal__close"),modalOverlay=document.querySelectorAll(".modal__overlay");for(let button of openModalBtn)button.addEventListener("click",openModal);function openModal(event){event.preventDefault();const btn=this,targetModal=btn.getAttribute("data-href"),childs=document.querySelector(`${targetModal}`).children;document.body.classList.add("no-scroll");for(let child of childs)child.classList.contains("modal__overlay")?child.classList.add("modal__overlay--visible"):child.classList.contains("modal__dialog")&&child.classList.add("modal__dialog--visible")}closeModalBtn.addEventListener("click",closeModal);for(let overlay of modalOverlay)overlay.addEventListener("click",closeModal);function closeModal(event){event.preventDefault();const modalOverlay=document.querySelector(".modal__overlay--visible"),modalDialog=document.querySelector(".modal__dialog--visible");modalOverlay.classList.remove("modal__overlay--visible"),modalDialog.classList.remove("modal__dialog--visible"),document.body.classList.remove("no-scroll")}document.addEventListener("keydown",event=>{"Escape"==event.key&&closeModal(event)});const bookmarks=document.querySelectorAll(".bookmark");bookmarks.forEach(item=>{item.addEventListener("click",(function(){this.classList.toggle("bookmark--active")}))})});